$(document).ready(function(){$(".tag").each(function(){let e=$(this).text(),t=0;for(let n=0;n<e.length;n++)t+=e.codePointAt(n);$(this).css("background-color",`hsl(${t%360}deg 100% var(--tag-light))`)})}),$(document).ready(function(){$(".project-img").click(function(){let e=$(this).attr("id");$(".popup").removeClass("active"),$(`#popup-${e.substring(e.indexOf("-")+1)}`).addClass("active")}),$(".popup-close").click(function(){$(".popup").removeClass("active")}),$(".overlay").click(function(){$(".popup").removeClass("active")})})