$(document).ready(()=>{let i=$(".intro > .description-text-group > .description-text").length,s=0,t=0,n=0,o=0,e=0,a=setInterval(()=>{if(e==0){let i=$(`.intro > .description-text-group > #description-text-${s}`).text();$(".description > .animated-text").text(i.substring(0,t)+"│"),n%2==0&&t++,t==i.length+1&&(e=1,o=n+50)}else if(e==1&&n==o)e=2;else if(e==2){let i=$(`.intro > .description-text-group > #description-text-${s}`).text();$(".description > .animated-text").text(i.substring(0,t)+"│"),t--,t==-1&&(e=3,o=n+50,s++)}else e==3&&n==o&&(e=0);n++,s==i&&(s=0)},25)})